<?php
   class hCalendarEventsShell extends hShell { private $hCalendarEvents; public function hConstructor() { $this->hCalendarEvents = $this->library('hCalendar/hCalendarEvents'); $hCalendarId = ($this->shellArgumentExists('-c', '--calendar'))? (int) $this->getShellArgumentValue('-c', '--calendar') : 1; $hCalendarCategoryId = ($this->shellArgumentExists('-y', '--category'))? (int) $this->getShellArgumentValue('-y', '--category') : 3; $count = ($this->shellArgumentExists('-n', '--count'))? (int) $this->getShellArgumentValue('-n', '--count') : 0; $date = ($this->shellArgumentExists('-d', '--date'))? strtotime($this->getShellArgumentValue('-d', '--date')) : time(); echo $this->hCalendarEvents->get($date, $hCalendarId, $hCalendarCategoryId, $count); } } ?>