<?php
  class hCalendarViewService extends hService { private $hCalendarView; public function hConstructor() {         $this->hCalendarView = $this->library('hCalendar/hCalendarView'); } public function get() {                                                                                  if (isset($_GET['hCalendarWeekday'])) { $this->hCalendarWeekday = $_GET['hCalendarWeekday']; } if (isset($_GET['hCalendarMonth'])) { $this->hCalendarMonth = substr($_GET['hCalendarMonth'], 0, 1); } if (isset($_GET['hCalendarDay'])) { $this->hCalendarDay = substr($_GET['hCalendarDay'], 0, 1); } if (isset($_GET['hCalendarYear'])) { $this->hCalendarYear = substr($_GET['hCalendarYear'], 0, 1); } $this->HTML( $this->hCalendarView->get( isset($_GET['hCalendarDate'])? $_GET['hCalendarDate'] : null, isset($_GET['hCalendarUniqueId'])? $_GET['hCalendarUniqueId'] : 'hCalendar', isset($_GET['hCalendarOffset'])? $_GET['hCalendarOffset'] : 0 ) ); } } ?>