<?php
  class hDialogueService extends hService { private $hDialogue; private $hForm; public function hConstructor() {         $this->hForm = $this->library('hForm'); $this->hDialogue = $this->library('hDialogue'); $this->hDialogue->setForm($this->hForm); } public function getModalDialogue() {        $this->hDialogueClose = true; $this->hDialogueModal = true; $this->hDialogue->newDialogue('hFrameworkModal'); $this->hDialogueAction = '#'; $this->hForm->addDiv('hFrameworkModalDialogueDiv'); $this->hForm->addFieldset('', '100%', '100%'); $this->hForm->addTableCell(''); $this->hDialogue->addButtons('OK', 'Cancel'); $this->HTML( $this->hDialogue->getDialogue(null, '') ); $this->hDialogue->reset(); } public function getAlertDialogue() {                                 $this->hDialogueClose = false; $this->hDialogueModal = true; $this->hDialogue->newDialogue('hFrameworkAlert'); $this->hDialogueAction = '#'; $this->hForm->addDiv('hFrameworkAlertDiv'); $this->hForm->addFieldset('Alert', '100%', '100%'); $this->hForm->addTableCell($this->getTemplate('Alert')); $this->hDialogue->addButtons('OK'); $this->HTML( $this->hDialogue->getDialogue(null, 'Alert') ); } public function getConfirmDialogue() {                                     $this->hDialogueClose = false; $this->hDialogueModal = true; $this->hDialogue->newDialogue('hFrameworkConfirm'); $this->hDialogueAction = '#'; $this->hForm->addDiv('hFrameworkConfirmDiv'); $this->hForm->addFieldset('Confirm', '100%', '100%'); $this->hForm->addTableCell($this->getTemplate('Confirm')); $this->hDialogue->addButtons('OK', 'Cancel'); $this->HTML( $this->hDialogue->getDialogue(null, 'Confirmation') ); } public function getLoginDialogue() {                             $this->hDialogueClose = false; $this->hDialogueModal = true; $this->hDialogue->newDialogue('hFrameworkLogin'); $this->hDialogueAction = '#'; $this->hForm->addDiv('hFrameworkLoginDiv'); $this->hForm->addFieldset('Login', '100%', '185px,'); $this->hForm->setAttributes( array( 'autocapitalize' => 'off', 'autocorrect' => 'off', 'autofocus' => 'autofocus' ) ); $this->hForm->addTextInput('hFrameworkLoginUserName', $this->hUserLoginUserNameLabel('User Name or Email Address:'), 45); $this->hForm->addPasswordInput('hFrameworkLoginPassword', 'Password:', 45); $this->hForm->addTableCell(''); $this->hForm->addCheckboxInput('hFrameworkLoginCookie', 'Remember this login on this computer?'); $this->hForm->addTableCell($this->getTemplate('Login'), 2); $this->hDialogue->addButtons('Login', 'Cancel'); $this->HTML( $this->hDialogue->getDialogue(null, 'Login') ); } public function getPromptDialogue() {                                     $this->hDialogueClose = false; $this->hDialogueModal = true; $this->hDialogue->newDialogue('hFrameworkPrompt'); $this->hDialogueAction = '#'; $this->hForm->addDiv('hFrameworkPromptDiv'); $this->hForm->addFieldset('Prompt', '100%', '100%'); $this->hForm->addTableCell($this->getTemplate('Prompt')); $this->hForm->addTextInput('hFrameworkPromptResponse', null, 25); $this->hDialogue->addButtons('OK', 'Cancel'); $this->HTML( $this->hDialogue->getDialogue(null, 'Prompt') ); } } ?>