<?php
  class hFileJSCompressLibrary extends hPlugin { private $packer; public function hConstructor() {  include_once $this->hFrameworkLibraryPath.'/Packer/class.JavaScriptPacker.php'; } public function get($path) { $dontCompress = false; $basePath = $this->getEndOfPath($path, $this->hFrameworkPath);  switch (true) { case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/fckeditor'): case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/Ace'): case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/dp.SyntaxHighlighter'): case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/SyntaxHighlighter'): case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/jQuery/Plugins/Threesixty'): case $this->beginsPath($basePath, $this->hFrameworkLibraryRoot.'/jQuery/Plugins/Reel'): { $dontCompress = true; break; } } if (basename($path) == 'jCrop.js') { $dontCompress = true; } $file = file_get_contents($path); if (strstr($path, '.template.js')) { $file = $this->parseTemplate($file); } if ($this->hFrameworkCompressJS(true) && !isset($_GET['noCompression']) && !$dontCompress) {  $packer = new JavaScriptPacker($file, 'Normal', true, false);  return $packer->pack(); } return $file; } } ?>