<?php
  class hFinderDialogueSaveAs extends hPlugin implements hFinderDialogueTemplate { public function hConstructor() { $this->getPluginFiles(); $this->hFinderHasFiles = true; $this->hFileTitle = 'Save As...'; $this->hFinderButtons = true; $this->hFinderButtonUpload = true; $this->hFinderButtonsRight = false; } public function getControls() { return $this->getTemplate( 'Buttons', array( 'hFileName' => isset($_GET['hFileName'])? $_GET['hFileName'] : '' ) ); } } ?>