<?php
  class hMapServiceGoogleLibrary extends hMapInterface { private $hJSON; public function hConstructor(array $arguments = array()) { if (!class_exists('hJSONLibrary')) { include $this->hServerDocumentRoot.'/hJSON/hJSON.library.php'; } $this->hJSON = new hJSONLibrary('/hJSON/hJSON.library.php'); } public function getCoordinates($query) {        $query = is_array($query)? urlencode(implode(',', $query)) : urlencode($query); $json = $this->hJSON->getJSON( 'http://maps.googleapis.com/maps/api/geocode/json?address='.$query.'&sensor=false' ); if (isset($json->results[0]) && isset($json->results[0]->geometry) && isset($json->results[0]->geometry->location)) { return array( 'latitude' => $json->results[0]->geometry->location->lat, 'longitude' => $json->results[0]->geometry->location->lng ); } return false; } public function getAddressCoordinates($address) {        return $this->getCoordinates($address); } public function getZipCodeCoordinates($zipCode) {        return $this->getCoordinates($zipCode); } public function getCityCoordinates($city) {        return $this->getCoordinates($city); } } ?>