<?php
  class hPluginInstallLibrary extends hPlugin { private $hPluginInstallFromZip; private $hPluginInstallFromJSON; public function hConstructor($arguments) { if ($arguments['mime']) { switch (true) { case substr($arguments['mime'], 0, 5) == 'text/': case $arguments['mime'] == 'application/json': case $arguments['mime'] == 'text/plain': case $arguments['mime'] == 'application/octet-stream': { $this->hPluginInstallFromJSON = $this->library( 'hPlugin/hPluginInstall/hPluginInstallFromJSON', $arguments ); break; } default: { $this->hPluginInstallFromZip = $this->library( 'hPlugin/hPluginInstall/hPluginInstallFromZip', $arguments ); } } } } } ?>