<?php
  class hUserImportShell extends hShell { private $hUserImport; public function hConstructor() { $this->hUserImport = $this->library('hUser/hUserImport'); if (!function_exists('json_decode')) {  $this->setToPHP4(); include_once 'Services/JSON.php'; } if (!$this->shellArgumentExists('from', '--from')) { $this->fatal( 'Import users failed because the from path was not specified.', __FILE__, __LINE__ ); } $from = $this->getShellArgumentValue('from', '--from'); if (file_exists($from)) { $this->fatal( 'Import users failed because the save path already exists.', __FILE__, __LINE__ ); } } } ?>