<?php
  class hUserLogin extends hPlugin { public function hConstructor() { $this->hUserLoginForm = true; if (!$this->hUserRegisterRedirect(null)) { $this->hUserRegisterRedirect = $this->hFilePath; } if (!$this->isLoggedIn()) { if (isset($_POST['hUserLoginExists']) && empty($_POST['hUserLoginExists'])) { $this->plugin('hUser/hUserRegister'); } else {  $this->hFileTitle = $this->hUserLoginTitle('Sign In'); $this->hFileDocument = $this->getLoginForm(); } } } } ?>