<?php
  class hUserSelectLibrary extends hPlugin { public function hConstructor() { $this->getPluginFiles(); $this->getPluginCSS('ie6'); } public function get($id, $isGroup = false) { $contactAddressBookId = 1; if (isset($this->hUserSelectConfiguration) && isset($this->hUserSelectConfiguration->$id)) { if (isset($this->hUserSelectConfiguration->$id->hContactAddressBookId)) { $contactAddressBookId = (int) $this->hUserSelectConfiguration->$id->hContactAddressBookId; } } return $this->getTemplate( 'Select', array( 'userSelectId' => $id, 'userGroupSwitch' => $isGroup? 'group' : 'user', 'contactAddressBookId' => $contactAddressBookId ) ); } } ?>